import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class PlayerContainer extends React.Component {
    render() {
        return (
            <div className="player-container">
            </div>
        );
    }
}

class GameContainer extends React.Component {
    render() {
        return (
            <div className="game-container">
            </div>
        );
    }
}

class Boxscore extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gameStatus: '',
            league: '',
            gameInfo: {},
            playerInfo: {}
        }
    }

    async getJson(url) {
        // let data = await fetchUrlData(url);

        this.setState({
            gameStatus: '',
            league: '',
            scoreInfo: {},
            playerInfo: {},
            teams: {},
            location: {}
        });
    }

    async getGameInfo(leagueType) {
        const gameInfo = await getGameInfo(leagueType);
        const gameScore = await getGameScore(leagueType);
        const teamInfo = await getTeamInfo(leagueType);
        let stateObj = {
            league: leagueType
        };

        stateObj.gameStatus = gameInfo.status;
        stateObj.scoreInfo = gameScore;
        stateObj.teams = {
            home: teamInfo.home,
            away: teamInfo.away
        }
        stateObj.location = teamInfo.location;
        console.log(stateObj);
    }

    render() {
        // This is how you get data
        // setTimeout(() => {
        //     this.getJson('/api/games/nba?stats=home_team');
        // }, 5000);
        this.getGameInfo('MLB');

        return (
            <div className="boxscore-container">
                <GameContainer />
                <PlayerContainer />
            </div>
        );
    }
}

ReactDOM.render(
    <Boxscore />,
    document.getElementById('root')
);

/* ========== HELPER FUNCTIONS ========== */

function getGameInfo(leagueType) {
    const apiEndpoint = apiURLConstructor(leagueType, 'event_information')

    return fetchUrlData(apiEndpoint);
}

async function getGameScore(leagueType) {
    const homeEndpoint = apiURLConstructor(leagueType, 'home_period_scores');
    const awayEndpoint = apiURLConstructor(leagueType, 'away_period_scores');
    const homeData = await fetchUrlData(homeEndpoint);
    const awayData = await fetchUrlData(awayEndpoint);
    const returnObj = {
        home: homeData,
        away: awayData
    }

    return returnObj;
}

async function getTeamInfo(leagueType) {
    const homeEndpoint = apiURLConstructor(leagueType, 'home_team');
    const awayEndpoint = apiURLConstructor(leagueType, 'away_team');
    const homeData = await fetchUrlData(homeEndpoint);
    const awayData = await fetchUrlData(awayEndpoint);
    const returnObj = {
        home: constructTeamObj(homeData),
        away: constructTeamObj(awayData),
        location: constructLocObj(homeData)
    }

    return returnObj;
}

function constructTeamObj(obj) {
    return {
        name: obj.full_name,
        abbv: obj.abbreviation
    };
}

function constructLocObj(obj) {
    return {
        name: `${obj.city}, ${obj.state}`,
        stadium: obj.site_name
    };
}

/**
 * @name fetchUrlData
 * @param {String} url API endpoint to hit
 * @returns {void}
 */
function fetchUrlData(url) {
    return new Promise((res, rej) => {
        // Get our endpoint data
        fetch(url)
            .then((resp) => {
                // If it came back propertly, return it
                if (resp.status === 200) {
                    res(resp.json());
                // Otherwise send the error
                } else {
                    rej(resp.statusText);
                }
            })
            .catch((err) => {
                rej(err);
            });
    });
}

/**
 * @name apiURLConstructor
 * @param {String} league NBA vs. MLB
 * @param {String} typeOfStat What aggregator stat would you like
 */
function apiURLConstructor(league, typeOfStat) {
    let baseURL = '/api/games';
    const acceptedStats = {
        away_team: true,
        home_team: true,
        away_period_scores: true,
        home_period_scores: true,
        away_stats: true,
        home_stats: true,
        officials: true,
        event_information: true,
        away_totals: true,
        home_totals: true
    }

    if (league) {
        baseURL += `/${league.toUpperCase()}`;
    }

    if (typeOfStat && acceptedStats[typeOfStat]) {
        baseURL += `?stats=${typeOfStat}`;
    }

    return baseURL;
}
