import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import constants from './constants.js'

class PlayerContainer extends React.Component {
    constructStatPlayer(playerObj) {
        const stat = constants.playerStats[this.props.league].searchBy
        let playerPerformance = '';
        playerPerformance += `${capitalizeString(stat)}: ${playerObj[stat]}`;

        return (
            <div className="player-card">
                <div className="stats">
                    <div className="player-divider">
                        {this.createPlayerName(playerObj.display_name, playerObj.team_abbreviation)}
                    </div>
                    <div className="player-divider">
                        {playerPerformance}
                    </div>
                    {
                        constants.playerStats[this.props.league].alsoShow.length > 0 &&
                        <div className="player-divider">
                            {this.createPlayerStats(playerObj, constants.playerStats[this.props.league].alsoShow)}
                        </div>
                    }
                </div>
            </div>
        );
    }

    constructCategoryPlayer(category, playerObj) {
        const alsoShow = constants.playerStats[this.props.league].alsoShow;

        if (alsoShow.length > 0) {
            return (
                <div className="player-card">
                    <div className="stats">
                        <div className="player-divider">
                            {this.createPlayerName(playerObj.display_name, playerObj.team_abbreviation)}
                        </div>
                        <div className="player-divider">
                            {category.toUpperCase()}
                        </div>
                        <div className="player-divider">
                            {this.createPlayerStats(playerObj, alsoShow)}
                        </div>
                    </div>
                </div>
            );
        } else {
            return (
                <div className="player-card">
                    <div className="stats">
                        <div className="player-divider">
                            {this.createPlayerName(playerObj.display_name, playerObj.team_abbreviation)}
                        </div>
                        <div className="player-divider">
                            {category.toUpperCase()}
                        </div>
                    </div>
                </div>
            );
        }
    }

    createPlayerStats(playerObj, stats) {
        let returnMsg = '';

        stats.forEach((stat) => {
            if (playerObj[stat]) {
                returnMsg += `${stat.toUpperCase().replace(/_/g, ' ')}: ${playerObj[stat]}, `;
            }
        });

        if (returnMsg.length > 0) {
            returnMsg = returnMsg.slice(0, -2);
        }

        return returnMsg;
    }

    createPlayerName (name, team) {
        return `${name} (${team})`;
    }

    render() {
        let players = [];

        if (Array.isArray(this.props.playerInfo)) {
            this.props.playerInfo.forEach((player) => {
                players.push(this.constructStatPlayer(player));
            });
        } else {
            for (let key in this.props.playerInfo) {
                if (this.props.playerInfo.hasOwnProperty(key)) {
                    players.push(this.constructCategoryPlayer(key, this.props.playerInfo[key]));
                }
            }
        }

        return (
            <div className="player-container">
                <div className="players">
                    {players}
                </div>
            </div>
        );
    }
}

class GameContainer extends React.Component {
    render() {
        return (
            <div className="game-container">
                <TeamColumn gameStatus={this.props.gameStatus}
                    teams={this.props.teams}
                    useSmall={this.props.useSmall} />
                <ScoreTable useSmall={this.props.useSmall}
                    gameScore={this.props.gameScore}
                    league={this.props.league}
                    extraStats={this.props.extraStats} />
            </div>
        );
    }
}

class ScoreTable extends React.Component {
    createHeaderRow(periodLen, extraStatHeaders) {
        const tempArr = Array.apply(null, Array(periodLen))
            .map((x, i) => { 
                return i + 1;
            });

        return (
            <tr>
                {
                    tempArr.map((val) => {
                        return <td className="score-cell">{this.generateHeaderLabel(val, false)}</td>;
                    })
                }
                {
                    extraStatHeaders.map((header) => {
                        return <td className="score-cell">{header}</td>
                    })
                }
            </tr>
        );
    }

    createScoreRow(scores, extraStats) {
        return (
            <tr>
                {
                    scores.map((val) => {
                        return <td className="score-cell">{val}</td>
                    })
                }
                {
                    extraStats.map((val) => {
                        if (val === '<TOTAL>') {
                            val = scores.reduce((acc, v) => acc + v);
                        }

                        return <td className="score-cell">{val}</td>
                    })
                }
            </tr>
        );
    }

    generateHeaderLabel(idx, isOT) {
        let returnStr = '';

        if (isOT) {
            returnStr += this.numReplace(constants.periods[this.props.league].OT, idx);
        } else if (this.props.useSmall) {
            returnStr += this.numReplace(constants.periods[this.props.league].small, idx);
        } else {
            returnStr += this.numReplace(constants.periods[this.props.league].normal, idx);
        }

        return returnStr;
    }

    numReplace(str, idx) {
        if (str.includes('<NUM>')) {
            return str.replace('<NUM>', idx);
        } else {
            return str.replace('<CARDNUM>', this.getNumberWithOrdinal(idx));
        }
    }

    getNumberWithOrdinal(n) {
        let s=["th","st","nd","rd"],
        v= n % 100;
        return n + (s[(v-20)%10]||s[v]||s[0]);
     }

    createTableData() {
        let table = [];
        const extraHeaders = this.props.extraStats.map((val) => {
            return Object.keys(val)[0];
        });

        table.push(this.createHeaderRow(this.props.gameScore.home.length, extraHeaders));
        table.push(this.createScoreRow(this.props.gameScore.away, generateTeamStatsArray(this.props.extraStats, 'away')));
        table.push(this.createScoreRow(this.props.gameScore.home, generateTeamStatsArray(this.props.extraStats, 'home')));

        return table;
    }

    render() {
        const tableData = this.createTableData();

        return (
            <div className="game-score">
                <table>
                    <tbody>
                        {tableData}
                    </tbody>
                </table>
            </div>
        );
    }
}

