class Boxscore extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gameStatus: '',
            league: '',
            scoreInfo: {away: [], home: []},
            playerInfo: {},
            teams: {away: '', home: ''},
            useSmall: false,
            extraStats: []
        }
    }

    componentWillUnmount() {
        window.removeEventListener('resize', () => {
            this.checkWindowSize()
        });
    }

    checkWindowSize() {
        let width = window.innerWidth;

        if ((width < 1150 && !this.state.useSmall) || (width >= 1150 && this.state.useSmall)) {
            this.updateSizeState();
        }
    }

    updateSizeState() {
        const newState = this.state;
        newState.useSmall = !this.state.useSmall;

        this.setState(newState);
    }

    async getGameInfo(leagueType) {
        const gameInfo = await getGameInformation(leagueType);
        const gameScore = await getGameScore(leagueType);
        const teamInfo = await getTeamInfo(leagueType);
        const playerInfo = await getPlayerInfo(leagueType);
        const extraStats = await getExtraStats(leagueType);
        let stateObj = {
            league: leagueType
        };

        stateObj.gameStatus = gameInfo.status;
        stateObj.location = gameInfo.location;
        stateObj.scoreInfo = gameScore;
        stateObj.teams = teamInfo;
        stateObj.playerInfo = playerInfo;
        stateObj.extraStats = extraStats;

        return stateObj;
    }

    componentDidMount() {
        this.getGameInfo('MLB')
            .then((newState) => {
                this.setState(newState);
            });

        window.addEventListener('resize', () => {
            this.checkWindowSize();
        });
    }

    render() {
        return (
            <div className="boxscore-container">
                <GameContainer gameStatus={this.state.gameStatus}
                               gameScore={this.state.scoreInfo}
                               teams={this.state.teams}
                               useSmall={this.state.useSmall}
                               league={this.state.league}
                               extraStats={this.state.extraStats}/>
                <PlayerContainer playerInfo={this.state.playerInfo}
                                 league={this.state.league} />
            </div>
        );
    }
}

