class Boxscore extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            gameStatus: '',
            league: '',
            gameInfo: {},
            playerInfo: {}
        }
    }

    async getGameInfo(leagueType) {
        const gameInfo = await getGameInfo(leagueType);
        const gameScore = await getGameScore(leagueType);
        const teamInfo = await getTeamInfo(leagueType);
        const playerInfo = await getPlayerInfo(leagueType);
        let stateObj = {
            league: leagueType
        };

        stateObj.gameStatus = gameInfo.status;
        stateObj.location = gameInfo.location;
        stateObj.scoreInfo = gameScore;
        stateObj.teams = teamInfo;
        stateObj.playerInfo = playerInfo;

        this.setState({
            stateObj
        });
    }

    componentDidMount() {
        this.getGameInfo('NBA');
    }

    render() {
        const stat = 'points';

        return (
            <div className="boxscore-container">
                <GameContainer gameStatus={this.state.gameStatus}
                               gameScore={this.state.scoreInfo}
                               teams={this.state.teams}/>
                <PlayerContainer playerInfo={this.state.playerInfo}
                                 stat={stat}/>
            </div>
        );
    }
}

ReactDOM.render(
    <Boxscore />,
    document.getElementById('root')
);

/* ========== HELPER FUNCTIONS ========== */

async function getGameInfo(leagueType) {
    const apiEndpoint = apiURLConstructor(leagueType, 'event_information')
    const eventData = await fetchUrlData(apiEndpoint);
    const returnObj = {
        status: eventData.status,
        location: constructLocObj(eventData.site)
    };

    return returnObj
}

async function getGameScore(leagueType) {
    const homeEndpoint = apiURLConstructor(leagueType, 'home_period_scores');
    const awayEndpoint = apiURLConstructor(leagueType, 'away_period_scores');
    const homeData = await fetchUrlData(homeEndpoint);
    const awayData = await fetchUrlData(awayEndpoint);
    const returnObj = {
        home: homeData,
        away: awayData
    }

    return returnObj;
}

async function getTeamInfo(leagueType) {
    const homeEndpoint = apiURLConstructor(leagueType, 'home_team');
    const awayEndpoint = apiURLConstructor(leagueType, 'away_team');
    const homeData = await fetchUrlData(homeEndpoint);
    const awayData = await fetchUrlData(awayEndpoint);
    const returnObj = {
        home: constructTeamObj(homeData),
        away: constructTeamObj(awayData)
    }

    return returnObj;
}

async function getPlayerInfo(leagueType) {
    const playerStatCategories = getLeagueStatCategory(leagueType);
    const homeEndpoint = apiURLConstructor(leagueType, playerStatCategories[0]);
    const awayEndpoint = apiURLConstructor(leagueType, playerStatCategories[1]);
    const homePlayerData = await fetchUrlData(homeEndpoint);
    const awayPlayerData = await fetchUrlData(awayEndpoint);
    const playerDataObj = {
        home: homePlayerData,
        away: awayPlayerData
    };
    const finalPlayerObj = createPlayerDataObj(leagueType, playerDataObj);

    return finalPlayerObj;
}

async function createPlayerDataObj(leagueType, playerDataObj) {
    const keyStats = {
        NBA: 'points',
        MLB: ['win', 'loss', 'save']
    }

    if (!Array.isArray(keyStats[leagueType])) {
        const stat = keyStats[leagueType];
        const rankedPlayers = getNHighestTotals(playerDataObj.home, playerDataObj.away, stat, 3);

        return rankedPlayers
    } else {
        const statsToFind = keyStats[leagueType];
        const players = findPlayerStats(statsToFind, playerDataObj.home, playerDataObj.away);

        return players;
    }
}

function findPlayerStats(statsArr, arr1, arr2) {
    const totalPlayers = arr1.concat(arr2);
    let returnObj = {};

    for (let entry of totalPlayers) {
        if (entry[statsArr[0]]) {
            returnObj[statsArr[0]] = entry;
        } else if (entry[statsArr[1]]) {
            returnObj[statsArr[1]] = entry;
        } else if (entry[statsArr[2]]) {
            returnObj[statsArr[2]] = entry;
        }

        // Max amount we can give back
        if (Object.keys(returnObj).length === 3) {
            break;
        }
    }

    return returnObj;
}

function getNHighestTotals(arr1, arr2, stat, n) {
    const totalPlayers = arr1.concat(arr2);

    return totalPlayers
        .sort((a, b) => statComparison(a, b, stat))
        .slice()
        .filter((val, i) => {
            return i < n;
        });
}

function statComparison(a, b, stat) {
    if (b[stat] > a[stat]) {
        return 1;
    } else if (a[stat] > b[stat]) {
        return -1;
    } else {
        return 0;
    }
}

function getLeagueStatCategory(leagueType) {
    if (leagueType === 'NBA') {
        return ['home_stats', 'away_stats'];
    } else {
        return ['home_pitchers', 'away_pitchers']
    }
}

function constructTeamObj(obj) {
    return {
        name: obj.full_name,
        abbv: obj.abbreviation
    };
}

function constructLocObj(obj) {
    return {
        name: `${obj.city}, ${obj.state}`,
        stadium: obj.name
    };
}

/**
 * @name fetchUrlData
 * @param {String} url API endpoint to hit
 * @returns {void}
 */
function fetchUrlData(url) {
    return new Promise((res, rej) => {
        // Get our endpoint data
        fetch(url)
            .then((resp) => {
                // If it came back propertly, return it
                if (resp.status === 200) {
                    res(resp.json());
                // Otherwise send the error
                } else {
                    rej(resp.statusText);
                }
            })
            .catch((err) => {
                rej(err);
            });
    });
}

/**
 * @name apiURLConstructor
 * @param {String} league NBA vs. MLB
 * @param {String} typeOfStat What aggregator stat would you like
 */
function apiURLConstructor(league, typeOfStat) {
    let baseURL = '/api/games';

    if (league) {
        baseURL += `/${league.toUpperCase()}`;
    }

    if (typeOfStat) {
        baseURL += `?stats=${typeOfStat}`;
    }

    return baseURL;
}
