import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            jsonReturn: this.getJson()
                        .then((data) => {
                            console.log(data);
                        })
        }
    }

    async getJson() {
        const response = await fetch('/api/games/nba?stats=home_team');
        const body = await response.json();

        if (response.status !== 200) {
            throw Error(body.message)
        }

        return body;
    }

    render() {
        return (
            <div className="app-container">
                {this.state.jsonReturn}
            </div>
        );
    }
}

// ========================================

ReactDOM.render(
    <Board />,
    document.getElementById('root')
);
