}

ReactDOM.render(
    <Boxscore />,
    document.getElementById('root')
);

/* ========== HELPER FUNCTIONS ========== */

async function getIndividualGame(leagueType) {
    const gameData = await fetchUrlData(`api/games/${leagueType.toUpperCase()}`);

    return gameData;
}

/**
 * @name fetchUrlData
 * @param {String} url API endpoint to hit
 * @returns {void}
 */
function fetchUrlData(url) {
    return new Promise((res, rej) => {
        // Get our endpoint data
        fetch(url)
            .then((resp) => {
                // If it came back propertly, return it
                if (resp.status === 200) {
                    res(resp.json());
                // Otherwise send the error
                } else {
                    rej(resp.statusText);
                }
            })
            .catch((err) => {
                rej(err);
            });
    });
}
