export default class Constants {
    constructor() {
        this.periods = {
            MLB: {
                normal: '<CARDNUM> Inning',
                small: '<NUM>',
                OT: '<NUM>',
                extras: [
                    {
                        'R': {
                            awayStat: 'away_batter_totals',
                            awaySubStat: 'runs',
                            homeStat: 'home_batter_totals',
                            homeSubStat: 'runs'
                        }
                    },
                    {
                        'H': {
                            awayStat: 'away_batter_totals',
                            awaySubStat: 'hits',
                            homeStat: 'home_batter_totals',
                            homeSubStat: 'hits'
                        }
                    },
                    {
                        'E': {
                            awayStat: 'away_errors',
                            awaySubStat: 'away_errors',
                            homeStat: 'home_errors',
                            homeSubStat: 'home_errors'
                        }
                    }
                ]
            },
            NBA: {
                normal: '<CARDNUM> Quarter',
                small: 'Q<NUM>',
                OT: 'OT<NUM>',
                extras: [
                    {
                        'T': {
                            awayStat: '<TOTAL>',
                            homeStat: '<TOTAL>',
                            awaySubStat: false,
                            homeSubStat: false
                        }
                    }
                ]
            }
        };
        
        this.playerStats = {
            NBA: {
                searchBy: 'points',
                alsoShow: ['assists', 'defensive_rebounds', 'offensive_rebounds']
            },
            MLB: {
                searchBy: ['win', 'loss', 'save'],
                alsoShow: ['innings_pitched', 'earned_runs', 'strike_outs', 'era']
            }
        };
        
        this.sharedValues = {
            eventInfo: 'event_information',
            homeScore: 'home_period_scores',
            awayScore: 'away_period_scores',
            homeTeamInfo: 'home_team',
            awayTeamInfo: 'away_team'
        };
        
        this.playerIdentifiers = {
            NBA: {
                away: 'away_stats',
                home: 'home_stats',
            },
            MLB: {
                away: 'away_pitchers',
                home: 'home_pitchers'
            }
        };
    }

    get sharedValues() {
        return this.sharedValues;
    }
}
