import React from 'react';
import ReactDOM from 'react-dom';
import './index.css';
import { resolve } from 'path';

class Board extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            jsonReturn: this.getJson('/api/games/nba?stats=home_team')
        }
    }

    async getJson(url) {
        let data = await fetchUrlData(url);

        return data;
    }

    render() {
        return (
            <div className="app-container">
                {this.state.jsonReturn}
            </div>
        );
    }
}

// ========================================

ReactDOM.render(
    <Board />,
    document.getElementById('root')
);

function fetchUrlData(url) {
    return new Promise((res, rej) => {
        fetch(url)
            .then((data) => {
                if (data) {
                    resolve(data);
                }
            })
            .catch((err) => {
                rej(err);
            });
    });
}
