const constants = {
    periods: {
        MLB: {
            extras: [
                {
                    'R': {
                        awayStat: 'away_batter_totals',
                        awaySubStat: 'runs',
                        homeStat: 'home_batter_totals',
                        homeSubStat: 'runs'
                    }
                },
                {
                    'H': {
                        awayStat: 'away_batter_totals',
                        awaySubStat: 'hits',
                        homeStat: 'home_batter_totals',
